export const initialState = {
    basket: new Map(),
    user: null
  };

  // Selector
  export const getBasketTotal = (basket) => 
    basket?.reduce((amount, item) => item.price + amount, 0);

  const reducer = (state, action) => {
    console.log(action);
    switch (action.type) {
        case "ADD_TO_BASKET":
            const quantity = state.basket.has(action.item.id)? 1 : state.basket.get(action.item.id).quantity + 1;
            basket.set(action.item)
            return {
                ...state,
                basket: [...state.basket, action.item],
              };
      default:
        return state;
    }
  };

  export default reducer;
