import React, { useState } from "react";
import guitar from "./Images/guitar.jpg";
import drums from "./Images/drums.jpg";
import kidsGuitar from "./Images/kids-guitar.jpg";
import saxophone from "./Images/saxophone.jpg";
import "./Carousel.css";

const Carousel = () => {
  const slides = [
    {
      src: guitar,
      title: "Find your passion",
    },
    {
      src: drums,
      title: "Feel the beat",
    },
    { src: saxophone, title: "Relax with jazz" },
    { src: kidsGuitar, title: "See what works for your little ones" },
  ];
  const [currIndex, setCurrIndex] = useState(2);
  return (
    <div
      className="carousel"
      style={{
        backgroundImage: `url(${slides[currIndex].src})`,
      }}
    ></div>
  );
};

export default Carousel;
