import React, { useState } from "react";
import guitar from "./Images/guitar.jpg";
import drums from "./Images/drums.jpg";
import kidsGuitar from "./Images/kids-guitar.jpg";
import saxophone from "./Images/saxophone.jpg";
import ArrowForwardIosIcon from "@mui/icons-material/ArrowForwardIos";
import ArrowBackIosIcon from "@mui/icons-material/ArrowBackIos";
import FiberManualRecordOutlinedIcon from "@mui/icons-material/FiberManualRecordOutlined";
import CircleIcon from "@mui/icons-material/Circle";

import "./Carousel.css";

const Carousel = () => {
  const slides = [
    {
      src: guitar,
      caption: (
        <RTCEncodedV>
          <h1
            className="carousel__captionTitle"
            style={{ gridRow: "4/5", gridColumn: "2" }}
          >
            Find your Passion
          </h1>
          <button>See Guitars</button>
        </RTCEncodedV>
      ),
    },
    {
      src: drums,
      caption: (
        <React.Fragment>
          <h1
            className="carousel__captionTitle"
            style={{ gridRow: "4/5", gridColumn: "2" }}
          >
            Feel the Beat
          </h1>
          <button>See Percussion</button>
        </React.Fragment>
      ),
    },
    {
      src: saxophone,
      caption: (
        <React.Fragment>
          <h1
            className="carousel__captionTitle"
            style={{ gridRow: "4/5", gridColumn: "2" }}
          >
            Relax with Jazz
          </h1>
          <button>See Saxophones</button>
        </React.Fragment>
      ),
    },
    {
      src: kidsGuitar,
      caption: (
        <React.Fragment>
          <h1
            className="carousel__captionTitle"
            style={{ gridRow: "4/5", gridColumn: "2" }}
          >
            See what works for your little ones
          </h1>
          <button>See Junior Instruments</button>
        </React.Fragment>
      ),
    },
  ];
  const [currIndex, setCurrIndex] = useState(0);

  const decrementIndex = () => {
    if (currIndex == 0) {
      setCurrIndex(slides.length - 1);
    } else {
      setCurrIndex(currIndex - 1);
    }
  };

  const incrementIndex = () => {
    setCurrIndex((currIndex + 1) % slides.length);
  };
  return (
    <div
      className="carousel"
      style={{
        backgroundImage: `url(${slides[currIndex].src})`,
      }}
    >
      <div className="carousel__inner">
        <div className="carousel__left" onClick={decrementIndex}>
          <ArrowBackIosIcon className="backArrow" />
        </div>
        <div className="carousel__center">
          {slides[currIndex].caption}
          <div className="carousel__slideMarkers">
            {slides.map((_, i) => {
              if (i == currIndex) {
                return <CircleIcon className="carousel__activeMarker" />;
              } else {
                return (
                  <FiberManualRecordOutlinedIcon
                    className="carousel__inactiveMarker"
                    onClick={() => setCurrIndex(i)}
                  />
                );
              }
            })}
          </div>
        </div>
        <div className="carousel__right" onClick={incrementIndex}>
          <ArrowForwardIosIcon className="forwardArrow" />
        </div>
      </div>
    </div>
  );
};

export default Carousel;
