export const initialState = {
    basket: [],
    user: null
  };

  // Selector
  export const getBasketTotal = (basket) => 
    basket?.reduce((amount, item) => item.price + amount, 0);

  const reducer = (state, action) => {
    console.log(action);
    switch (action.type) {
        case "ADD_TO_BASKET":
            action.item.quantity = state.basket.has(action.item.id)? 1 : state.basket.get(action.item.id).quantity + 1;
            state.basket.set(action.item.id, action.item);
            return state;
      default:
        return state;
    }
  };

  export default reducer;
